import React from "react";
import { Link } from "react-router-dom";
//import Introduction from '../components/Introduction';
//import DisplayedApartments from '../components/DisplayedApartments';
import Banner from "../components/Banner";
import { Button, makeStyles, Box } from "@material-ui/core";


const useStyles = makeStyles(() => ({
  wrapper: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
  },
  button: {
    color: "white",
    fontSize: "1.5rem",
    background:'#D1B7A0',
    position: 'absolute',
    top: '50%',
    left: '50%',
    border: 'none',
    cursor: 'pointer',
    borderRadius: '5px',
    textAlign: 'center',
    transform: 'translate(-50%, -50%)',


  },
  box:{
    display: 'flex',
    justifyContent:'center',

  }
}));

const Home = () => {
  const classes = useStyles();
  return (
    <>
      <Box className={classes.wrapper} />
      <Banner>
        <Box className={classes.box}>
        <img style={{height:'auto', maxWidth:'100%'}} src="https://images.unsplash.com/photo-1582419445421-26ecbe11decf?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1557&q=80"/>       
          <Link style={{ textDecoration: "none" }} to="/apartments">
            <Button variant={"h1"} variant="contained" className={classes.button}>
              Rent apartment
            </Button>
          </Link>
        </Box>
      </Banner>
      <Box />
    </>
  );
};

export default Home;
