import React from "react";
import { Link } from "react-router-dom";
import defaultImg from "../images/apartmentcityview.jpg";
import PropTypes from "prop-types";
import { memo } from "react";
import { Box, Typography} from "@material-ui/core";

const Apartment = memo(({ apartment }) => {
  const { name, slug, images, price } = apartment;

  return (
    <Box>
      <Box>
        <img src={images[0] || defaultImg} alt="one room" style={{maxWidth: '100%'}}/>
        <Box>
          <Typography variant={'h2'}>â‚¬{price}</Typography>
          <p>per month</p>
        </Box>
        <Link to={`/apartments/${slug}`}>
          Features
        </Link>
      </Box>
      <p>{name}</p>
    </Box>
  );
});

Apartment.propTypes = {
  apartment: PropTypes.shape({
    name: PropTypes.string.isRequired,
    slug: PropTypes.string.isRequired,
    images: PropTypes.arrayOf(PropTypes.string).isRequired,
    price: PropTypes.number.isRequired
  })
};
export default Apartment;
