import React from "react";
import { useContext } from "react";
import { ApartmentContext } from "../context";

const getUnique = (items, value) => {
  return [...new Set(items.map(item => item[value]))];
};

const ApartmentsFilter = ({ apartments }) => {

  const context = useContext(ApartmentContext);
  const {
    handleChange,
    type,
    capacity,
    price,
    minPrice,
    maxPrice,
    minSize,
    maxSize,
    balcony,
    sauna
  } = context;


  let types = getUnique(apartments, "type");

  types = ["all", ...types];

  types = types.map((item, index) => (
    <option key={index} value={item}>
      {item}
    </option>
  ));

  let people = getUnique(apartments, "capacity");
  people = people.map((item, index) => (
    <option key={index} value={item}>
      {item}
    </option>
  ));

  return (
    <section>

      <form>
        {/* select type */}
        <div>
          <label htmlFor="type">room type</label>
          <select
            name="type"
            id="type"
            onChange={handleChange}
            className="form-control"
            value={type}
          >
            {types}
          </select>
        </div>
        {/* end of select type */}
        {/* guests  */}
        <div className="form-group">
          <label htmlFor="capacity">Guests</label>
          <select
            name="capacity"
            id="capacity"
            onChange={handleChange}
            className="form-control"
            value={capacity}
          >
            {people}
          </select>
        </div>
        {/* end of guests */}
        {/* room price */}
        <div className="form-group">
          <label htmlFor="price">room price ${price}</label>
          <input
            type="range"
            name="price"
            min={minPrice}
            max={maxPrice}
            id="price"
            value={price}
            onChange={handleChange}
            className="form-control"
          />
        </div>
        {/* end of room price*/}
        {/* size */}
        <div>
          <label htmlFor="price">room size </label>
          <div>
            <input
              type="number"
              name="minSize"
              value={minSize}
              onChange={handleChange}
            />
            <input
              type="number"
              name="maxSize"
              value={maxSize}
              onChange={handleChange}
            />
          </div>
        </div>
        <div>
          <div>
            <input
              type="checkbox"
              name="balcony"
              id="balcony"
              checked={balcony}
              onChange={handleChange}
            />
            <label>balcony</label>
          </div>
          <div>
            <input
              type="checkbox"
              name="sauna"
              checked={sauna}
              onChange={handleChange}
            />
            <label>sauna</label>
          </div>
        </div>
      </form>
    </section>
  );
};

export default ApartmentsFilter;
